import request from 'superagent';

const url='http://www.mocky.io/v2/5a3ab51031000085148e966f';

export const fetchPhones = async ()=>{
	const {body} = await request.get(url);
	return body.phones;
};

export const fetchPhoneId = async (id) => {
	const {body} = await request.get(url);
	for( let key in body.phones){
		if(body.phones[key].id===id){
			return body.phones[key];
		}
	}
};