import React from 'react';
import {Link} from 'react-router';

const PhonesItem = (props) =>{
	const {image,name,description,price,id} = props.phone;
	return(
		<div className='col-md-3 item__phone'>
			<img src={image} alt={name}/>
			<p className="description__tovar">{description}</p>
			<p className="price">Цена: ${price}</p>
			<p><Link to={`/phones/${id}`}>Страница товара</Link></p>
			<button className="btn btn-primary">Купить</button>
		</div>
	)
};

export default PhonesItem;