import R from 'ramda';
import {
	FETCH_PHONES_SUCCESS
} from 'constants';

const initialState={};

export default (state=initialState,{type,payload})=>{
	switch (type) {
		case FETCH_PHONES_SUCCESS:
			console.log(R.indexBy(R.prop('id'),payload));
			return R.merge(state,R.indexBy(R.prop('id'),payload));
		default:
			return state;
	}
}
